    public BebidasCadastro(Bebidas janelaBebidas, int codigo) {
        initComponents();
        this.janelaBebidas = janelaBebidas;
        this.codigo = codigo;
        if(codigo != -1) {
            this.setTitle("EDITAR BEBIDA");
            DB db = new DB("bancodados.db");
            db.query("SELECT * FROM bebidas WHERE codigo="+codigo);
            if(db.next()) {
                String fornecedor = db.getString("fornecedor");
                String produto = db.getString("produto");
                String alcoolico = db.getString("alcoolico");
                String tamanho = db.getString("tamanho");
                txtFornecedor.setText(fornecedor);
                txtProduto.setText(produto);
                txtAlcoolico.setText(alcoolico);
                txtTamanho.setText(tamanho);
            }
            db.closeConnection();        
        } else {
            this.setTitle("NOVA BEBIDA");
        }
    }
