    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String fornecedor = txtFornecedor.getText();
        String produto = txtProduto.getText();
        String alcoolico = txtAlcoolico.getText();
        String tamanho = txtTamanho.getText();
        DB db = new DB("bancodados.db");
        String query = "";
        if(codigo == -1) {
            query = "INSERT INTO bebidas (fornecedor, produto, alcoolico, tamanho) ";
            query = query + "VALUES (";
            query = query + "'" + fornecedor + "',";
            query = query + "'" + produto + "',";
            query = query + "'" + alcoolico + "',";
            query = query + "'" + tamanho + "'";
            query = query + ");";
        } else {
            query = "UPDATE bebidas SET ";
            query = query + "fornecedor='" + fornecedor + "', ";
            query = query + "produto='" + produto + "', ";
            query = query + "alcoolico='" + alcoolico + "', ";
            query = query + "tamanho='" + tamanho + "'";
            query = query + " WHERE codigo="+this.codigo;
        }
        db.execQuery(query);
        janelaBebidas.refreshTable();
        this.dispose();
    }//GEN-LAST:event_jButton2ActionPerformed

 
